import { useState } from 'react';
import Announcement from '../Components/Announcement';
import Footer from '../Components/Footer';
import NavBar from '../Components/NavBar';

export default function Create() {
    const [uploadedFile, setUploadedFile] = useState('');
    const [fileTitle, setFileTitle] = useState('');

    const handleFormSubmit = async (e) => {
        e.preventDefault();

        const form = document.getElementById('form');
        const formData = new FormData(form);

        await fetch('/api/product/upload', {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: JSON.stringify(formData),
        });

        // do something
        console.log('Form submitted');
    };

    function handleFileTitle(e) {
        setFileTitle(e.target.value);
    }

    function handleUploadedFile(e) {
        setUploadedFile(e.target.value);
    }
    return (
        <>
            <Announcement />
            <NavBar />
            <h1>File upload</h1>
            <form encType='multipart/form-data' onSubmit={handleFormSubmit} id='form'>
                <input type='file' name='uploadedFile' value={uploadedFile} onChange={handleUploadedFile} required />
                <br />
                <br />

                <label>File title:</label>
                <br />
                <input
                    type='text'
                    placeholder='Enter file title'
                    name='fileTitle'
                    value={fileTitle}
                    onChange={handleFileTitle}
                    required
                />
                <br />
                <br />

                <button type='submit'>Submit Form</button>
            </form>
            <Footer />
        </>
    );
}
