import { mockData } from '../data';
export default function Detail() {
    return (
        <>
            {mockData.map((item, index) => {
                return (
                    <div key={index}>
                        <p>Name: {item.name}</p>
                        <p>Color: {item.color}</p>
                        <p>price: {item.price}</p>
                        <img src={item.image} alt='product' />
                        <p>brand: {item.brand}</p>
                        <ul>
                            Tags
                            {item.tags.map((tag, index) => {
                                return <li key={index}>{tag}</li>;
                            })}
                        </ul>
                        <br />
                        <ul>
                            Configuration
                            {item.configuration.map((curItem, index) => {
                                return (
                                    <div key={index}>
                                        <li>{curItem.Screen}</li>
                                        <li>{curItem.OS}</li>
                                        <li>{curItem.Chip}</li>
                                        <li>{curItem.Size}</li>
                                    </div>
                                );
                            })}
                        </ul>
                        <br />
                        <ul>
                            Review
                            {item.review.map((curItem, index) => {
                                return (
                                    <div>
                                        <li key={index}>
                                            {curItem.user} - {curItem.comment}
                                        </li>
                                    </div>
                                );
                            })}
                        </ul>
                        <br />
                        <h1></h1>
                        <strong>{item.description}</strong>
                    </div>
                );
            })}
        </>
    );
}
