import styled from 'styled-components';
import Announcement from '../Components/Announcement';

const Container = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
`;

const Left = styled.div``;

const Right = styled.div``;

export default function Detail() {
    return (
        <>
            <Announcement />
            <Container>
                <Left>
                    <div>
                        <img
                            src='https://niche-24.woovinafree.com/wp-content/uploads/2018/01/product-02.jpg'
                            alt='main product'
                            style={{ height: '26rem', borderStyle: 'solid', borderWidth: 'thin', borderColor: 'gray' }}
                        />
                    </div>
                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', marginTop: '2rem' }}>
                        {/* loop here  */}
                        <img
                            src='https://niche-24.woovinafree.com/wp-content/uploads/2018/01/product-02-100x100.jpg'
                            alt='small product'
                            style={{
                                width: '100px',
                                height: '100px',
                                borderStyle: 'solid',
                                borderWidth: 'thin',
                                borderColor: 'gray',
                            }}
                        />
                    </div>
                </Left>
                <Right></Right>
            </Container>
        </>
    );
}
