import { useRef } from 'react';
import Announcement from '../Components/Announcement';
import Footer from '../Components/Footer';
import NavBar from '../Components/NavBar';

//* screen, OS, Back camera, Front Camera, Chip, Ram, SIM, Battery
export default function Create() {
    const tagsRef = useRef();
    const ScreenRef = useRef();
    const OSRef = useRef();
    const BackCameraRef = useRef();
    const FrontCameraRef = useRef();
    const ChipRef = useRef();
    const RamRef = useRef();
    const SimRef = useRef();
    const BatteryRef = useRef();

    // const tags = tagsRef.current.value.split(',');

    const handleSubmit = async (e) => {
        e.preventDefault();
        const form = document.querySelector('#form');
        const postProduct = await fetch(`/api/product`, {
            method: 'POST',
            body: new FormData(form),
        });
        postProduct();
    };
    return (
        <>
            <Announcement />
            <NavBar />
            <form id='form' encType='multipart/form-data' onSubmit={handleSubmit}>
                <input type='text' name='name' placeholder='name' />
                <input type='text' name='price' placeholder='price' />
                <input type='text' name='color' placeholder='color' />
                <input type='file' name='image' required />
                <input type='text' name='brand' placeholder='brand' />
                <input type='text' name='tags' placeholder='tags' ref={tagsRef} />
                <input type='text' name='screen' placeholder='screen' ref={ScreenRef} />
                <input type='text' name='screen' placeholder='screen' ref={ScreenRef} />

                <input type='text' name='description' placeholder='description' />
                <input type='submit' />
            </form>
            <Footer />
        </>
    );
}
