import { useAuthContext } from '../hooks/useAuthContext';

export const useLogout = () => {
    const { dispatch } = useAuthContext();
    const logout = async () => {
        localStorage.removeItem('user');
        console.log(object);
        dispatch({ type: 'LOGOUT' });
    };
    return { logout };
};
