export const useHistory = () => {
    const getHistory = async (id) => {
        const response = await fetch(`/api/history/${id}`);
        const json = await response.json();
        return json;
    };

    const postHistory = async (userID, productName, productColor, productSize, productPrice, productImage) => {
        const response = await fetch(`/api/history`, {
            method: 'POST',
            headers: { 'Content-type': 'application/json' },
            body: JSON.stringify({ userID, productName, productColor, productSize, productPrice, productImage }),
        });
        const json = await response.json();
        return json;
    };

    return { getHistory, postHistory };
};
